d3 = await import("https://cdn.jsdelivr.net/npm/d3@7/+esm")

labels = ["Government", "Corporations", "Households", "Rest of the World"];
size = labels.length;
cellSize = 100;
zoomScale = 2;
miniMatrixSize = 3;

viewof matrix = {
  const width = size * cellSize + 100;
  const height = size * cellSize + 100;
  const offsetX = 100;
  const offsetY = 100;

  const svg = d3.select(DOM.svg(width, height))
    .attr("style", "border:1px solid #ccc; font-family: sans-serif; background:#fff")
    .attr("viewBox", `0 0 ${width} ${height}`);

  // Zeilen-Labels (links)
  svg.selectAll(".row-label")
    .data(labels)
    .enter()
    .append("text")
    .attr("class", "row-label")
    .attr("x", offsetX - 10)
    .attr("y", (_, i) =&gt; offsetY + i * cellSize + cellSize / 2)
    .attr("text-anchor", "end")
    .attr("dominant-baseline", "middle")
    .attr("font-weight", "bold")
    .text(d =&gt; d);

  // Spalten-Labels (oben, gedreht)
  svg.selectAll(".col-label")
    .data(labels)
    .enter()
    .append("text")
    .attr("class", "col-label")
    .attr("transform", (_, i) =&gt; {
      const x = offsetX + i * cellSize + cellSize / 2;
      const y = offsetY - 10;
      return `translate(${x},${y}) rotate(-90)`;
    })
    .attr("text-anchor", "end")
    .attr("dominant-baseline", "middle")
    .attr("font-weight", "bold")
    .text(d =&gt; d);

  // Matrix-Daten vorbereiten
  const data = [];
  for (let row = 0; row &lt; size; row++) {
    for (let col = 0; col &lt; size; col++) {
      data.push({
        row,
        col,
        hoverZoom: row === 2 || col === 2 // Zeile ODER Spalte "Households"
      });
    }
  }

  const cells = svg.selectAll("g.cell")
    .data(data)
    .enter()
    .append("g")
    .attr("class", "cell")
    .attr("transform", d =&gt; `translate(${offsetX + d.col * cellSize}, ${offsetY + d.row * cellSize})`)
    .on("mouseover", function(event, d) {
      if (!d.hoverZoom) return;

      const [x, y] = [offsetX + d.col * cellSize, offsetY + d.row * cellSize];

      // Zoom auf Zelle
      svg.transition()
        .duration(300)
        .attr("viewBox", `${x - cellSize} ${y - cellSize} ${cellSize * zoomScale} ${cellSize * zoomScale}`);

      // Mini-Matrix anzeigen
      const cellGroup = d3.select(this);
      const miniCellSize = (cellSize - 20) / miniMatrixSize;
      const miniData = d3.range(miniMatrixSize * miniMatrixSize);

      const mini = cellGroup.append("g")
        .attr("class", "mini-matrix");

      mini.selectAll("rect")
        .data(miniData)
        .enter()
        .append("rect")
        .attr("x", i =&gt; (i % miniMatrixSize) * miniCellSize + 10)
        .attr("y", i =&gt; Math.floor(i / miniMatrixSize) * miniCellSize + 10)
        .attr("width", miniCellSize - 2)
        .attr("height", miniCellSize - 2)
        .attr("fill", "#ffe599")
        .attr("stroke", "#888");
    })
    .on("mouseout", function(event, d) {
      if (!d.hoverZoom) return;

      // Zoom zurücksetzen
      svg.transition()
        .duration(300)
        .attr("viewBox", `0 0 ${width} ${height}`);

      // Mini-Matrix entfernen
      d3.select(this).select("g.mini-matrix").remove();
    });

  // Zellen-Rechtecke
  cells.append("rect")
    .attr("width", cellSize - 4)
    .attr("height", cellSize - 4)
    .attr("fill", d =&gt; d.hoverZoom ? "#fff2cc" : "#f0f0f0")
    .attr("stroke", "#555");

  return svg.node();
}

