d3 = await import("https://cdn.jsdelivr.net/npm/d3@7/+esm")
Plot2 = await import("https://cdn.jsdelivr.net/npm/@observablehq/plot@0.6/+esm")

d3.csv("NASAsel.csv", d =&gt; ({
  time: d.time, // &lt;-- als String
  sector: d.sector,
  values: +d.values,
  pcntgdp: +d.pcntgdp
})).then(rawData =&gt; {

  const realData = rawData.filter(d =&gt; +d.time &lt;= 2021)

  const forecastDataRaw = rawData.filter(d =&gt; +d.time &gt;= 2022)

  const mergedSectors = ["S11", "S12", "S14", "S15"]
  const forecastSingle = forecastDataRaw.filter(d =&gt; !mergedSectors.includes(d.sector))

  const forecastMerged = d3.rollups(
    forecastDataRaw.filter(d =&gt; mergedSectors.includes(d.sector)),
    v =&gt; ({
      values: d3.sum(v, d =&gt; d.values),
      pcntgdp: d3.sum(v, d =&gt; d.pcntgdp)
    }),
    d =&gt; d.time
  ).map(([time, sums]) =&gt; ({
    time,
    sector: "S11–S12–S14–S15",
    values: sums.values,
    pcntgdp: sums.pcntgdp
  }))

  const finalData = realData.concat(forecastSingle).concat(forecastMerged)

  const customColors = {
    "S11": "#1f77b4",
    "S12": "#ff7f0e",
    "S13": "#2ca02c",
    "S14": "#d62728",
    "S15": "#9467bd",
    "S2": "#8c564b",
    "S11–S12–S14–S15": "#8888aa"
  }

  return Plot2.plot({
    marks: [
      // Echte Daten
      Plot2.barY(
        finalData.filter(d =&gt; +d.time &lt;= 2021),
        {
          x: "time",
          y: "pcntgdp",
          fill: "sector",
          title: d =&gt; `${d.sector}: ${d.pcntgdp.toFixed(2)} %`,
          tip: true,
          order: "sector"
        }
      ),
      // Forecast-Daten
      Plot2.barY(
        finalData.filter(d =&gt; +d.time &gt;= 2022),
        {
          x: "time",
          y: "pcntgdp",
          fill: "sector",
          title: d =&gt; `${d.sector}: ${d.pcntgdp.toFixed(2)} %`,
          tip: true,
          order: "sector",
          opacity: 0.5
          // kein stroke, kein strokeWidth
        }
      )
    ],
    color: {
      legend: true,
      label: "Sektor",
      domain: Object.keys(customColors),
      range: Object.values(customColors)
    },
    x: {
      label: "Jahr",
      tickFormat: d =&gt; d // jetzt String
    },
    y: {
      label: "% des BIP"
    },
    style: {
      fontSize: "12px",
      backgroundColor: "black" 
    },
    width: 500,
    height: 250
  });
});


























