Government Sector

Plot.plot({
  width: 500,
  height: 250,
  x: {
    label: "Jahr"
  },
  y: {
    label: "Wert (values)",
    grid: true
  },
  color: {
    label: "Item",
    legend: true
  },
  style: {backgroundColor: "black"},
  marks: [
    Plot.barY(
      // Filter auf S11 und sicherstellen, dass time ein String ist
      datNASAB9
        .filter(d =&gt; d.sector === "S13")
        .map(d =&gt; ({
          ...d,
          time: d.time.toString()
        })),
      {
        x: "time",
        y: "values",
        fill: "item",
        tip: true,
        // kein sort nötig bei Strings, außer du willst manuell sortieren
        // sort: {x: "time"}
      }
    )
  ]
})










