data = FileAttachment("Ovw.csv").csv({ typed: true })







Overview

filtered = data.filter(function(element) {
  return element.item == 'Real GDP';
})







Plot.plot({
  x: {
    label: null
  },
  y: {
    grid: true,
    label: "Unemployment (%)"
  },
  marks: [
    Plot.ruleY([0]),
    Plot.lineY(filtered, {
      x: "period",
      y: "value",
      stroke: "source",
      tip: true
    })
  ]
})








